  end
end
initial begin : proc_gen_input_V_internal_ready
    integer internal_trans_num;
    wait(AESL_reset === 0);
    wait(ready_initial === 1);
    input_V_ready_reg <= 0;
    @(posedge AESL_clock);
    internal_trans_num = 1;
    while(internal_trans_num != `AUTOTB_TRANSACTION_NUM + 1) begin
        if (ap_c_n_tvin_input_V_trans_num > internal_trans_num) begin
            input_V_ready_reg <= 1;
            @(posedge AESL_clock);
            input_V_ready_reg <= 0;
            internal_trans_num = internal_trans_num + 1;
        end
        else begin
            @(posedge AESL_clock);
        end
    end
    input_V_ready_reg <= 0;
end
initial begin : proc_ap_c_n_tvin_input_V_trans_num
    integer i;
    integer fp;
    integer ret;
    integer size;
    reg [127 : 0] token;
    fp = $fopen("./stream_size_in_input_V.dat","r");
    if(fp == 0) begin       // Failed to open file
	      $display("Failed to open file \"%s\"!","../tv/stream_size/stream_size_in_input_V.dat" );
        $display("ERROR: Simulation using HLS TB failed.");
	      $finish;
    end
    read_token(fp, token);
    if (token != "[[[runtime]]]") begin             // Illegal format
        $display("ERROR: Simulation using HLS TB failed.");
	      $finish;
    end
    read_token(fp, token);
    @(posedge AESL_clock);
    ap_c_n_tvin_input_V_trans_num = 1;
    while(token != "[[[/runtime]]]") begin
        i = 0;
        if (token != "[[transaction]]") begin             // Illegal format
            $display("ERROR: Simulation using HLS TB failed.");
	          $finish;
        end
        read_token(fp, token);     // skip transaction number
        read_token(fp, token);
        #0.5;
        //this is for hls::stream port
        ret = $sscanf(token, "%d", size);
        if (ret != 1) begin
            $display("Failed to parse token!");
            $display("ERROR: Simulation using HLS TB failed.");
            $finish;
        end
        while (i != size) begin
            if (input_V_read == 1) begin 
               i = i + 1;
           end 
           if (i != size) begin
               @(posedge AESL_clock);
               #0.5;
           end 
       end 
       read_token(fp, token);//[[/transaction]]
        read_token(fp, token);
        ap_c_n_tvin_input_V_trans_num = ap_c_n_tvin_input_V_trans_num + 1;
        if (i != 0) begin
            @(posedge AESL_clock);
        end 
    end
    if (token != "[[[/runtime]]]") begin             // Illegal format
        $display("ERROR: Simulation using HLS TB failed.");
	      $finish;
    end
    @(posedge AESL_clock);
    $fclose(fp);
end

